-- Schema and data for Trivia Questions

-- 1. Create the questions table
CREATE TABLE IF NOT EXISTS public.questions (
    id TEXT NOT NULL PRIMARY KEY,
    game_id TEXT NOT NULL,
    question TEXT NOT NULL,
    options TEXT[] NOT NULL,
    correct_answer TEXT NOT NULL,
    explanation TEXT,
    difficulty TEXT,
    category TEXT,
    image_url TEXT,
    image_alt TEXT,
    created_at TIMESTAMPTZ DEFAULT NOW(),
    updated_at TIMESTAMPTZ DEFAULT NOW()
);

-- 2. Enable RLS
ALTER TABLE public.questions ENABLE ROW LEVEL SECURITY;

-- 3. Create RLS Policies
-- Allow all authenticated users to read questions
CREATE POLICY "Allow authenticated read on questions" ON public.questions
    FOR SELECT TO authenticated USING (true);

-- 4. Create Indexes for performance
CREATE INDEX IF NOT EXISTS idx_questions_game_id ON public.questions(game_id);

-- 5. Insert the questions data
ON CONFLICT (id) DO NOTHING; 